import React, { useRef } from 'react';
import type { ActionType, ProColumns } from '@ant-design/pro-table';
import ProTable from '@ant-design/pro-table';
import { history, useModel } from 'umi';
import DelPopconfirm from '@/components/DelPopconfirm';
import { Button, Image, Input } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import { delProduct, queryProducts } from '@/services/Bas';
import { PageContainer } from '@ant-design/pro-layout';

const ProductsTable: React.FC<{
  columns?: ProColumns<BAS.CustType>[];
}> = () => {
  const actionRef = useRef<ActionType>();
  const columns: ProColumns<BAS.Spu>[] = [
    {
      dataIndex: 'index',
      valueType: 'index',
      width: 50,
      align: 'center',
    },
    {
      dataIndex: 'albumList',
      width: 50,
      align: 'center',
      render: (_, record) => (
        <Image
          src={(record.albumList as unknown) as string}
          fallback="data:image/png;base64,"
        ></Image>
      ),
    },
    {
      title: '商品名称',
      dataIndex: 'spuName',
    },
    {
      title: '商品编码',
      dataIndex: 'barCode',
    },
    {
      title: '分类',
      dataIndex: 'cateName',
    },
    {
      title: '规格',
      dataIndex: 'spec',
    },
    {
      title: '单位',
      dataIndex: 'unitName',
    },
    {
      title: '状态',
      dataIndex: 'state',
      valueType: 'select',
      valueEnum: () => {
        return new Map([
          [1, { text: '正常', status: 'Success' }],
          [0, { text: '禁用', status: 'Error' }],
        ]);
      },
    },
  ];
  columns.push({
    title: '操作',
    key: 'action',
    valueType: 'option',
    render: (_, entity, _index, action) => {
      return [
        <a
          key="editable"
          onClick={() => {
            history.push(`/bas/product/${entity.spuId}`);
          }}
        >
          修改
        </a>,
        <DelPopconfirm
          key="del"
          onConfirm={async () => {
            await delProduct([entity.spuId]);
            action.reload();
          }}
        />,
      ];
    },
  });
  return (
    <>
      <ProTable<BAS.Spu>
        size="small"
        search={false}
        rowKey="spuId"
        actionRef={actionRef}
        bordered
        toolBarRender={() => [
          <Button
            type="primary"
            onClick={() => {
              history.push('/bas/product/new');
            }}
          >
            <PlusOutlined />
            新建
          </Button>,
        ]}
        columns={columns}
        request={async (params) => {
          const response = await queryProducts({
            ...params,
            pageNumber: params.current,
            dev: true,
          });
          return {
            data: response.data.rows,
            success: response.code === 0,
            total: response.data.total,
          };
        }}
      />
    </>
  );
};
export default () => {
  const { globalDataLoaded } = useModel('@@initialState', (model) => ({
    globalDataLoaded: model.initialState?.globalDataLoaded,
  }));
  return globalDataLoaded && <PageContainer ghost content={<ProductsTable />}></PageContainer>;
};
