import React, { useRef, useState } from 'react';
import type { ActionType, ProColumns } from '@ant-design/pro-table';
import ProTable from '@ant-design/pro-table';
import { history } from 'umi';
import { Image } from 'antd';
import { delProduct, queryProducts } from '@/services/Bas';
import { PageContainer } from '@ant-design/pro-layout';
import {
  baseSearch,
  indexColumns,
  optionColumns,
  spuCodeColumns,
  stateColumns,
  tableAlertOptionRenderDom,
} from '@/utils/columns';
import BatchDel from '@/components/DelPopconfirm';
import GlobalWrapper from '@/components/GlobalWrapper';
import { Typography } from 'antd';
import ProCard from '@ant-design/pro-card';
import ProdcutTypeTable from '../productType';

const { Link } = Typography;
const ProductsTable: React.FC<{
  cateId?: K;
}> = ({ cateId }) => {
  const actionRef = useRef<ActionType>();
  const columns: ProColumns<BAS.Spu>[] = [
    indexColumns,
    {
      title: '商品名称',
      dataIndex: 'spuId',
      width: 150,
      fixed: 'left',
      render: (_, record) => (
        <Link
          onClick={() => {
            history.push(`/bas/product/${record.spuId}`);
          }}
        >
          {record.spuName}
        </Link>
      ),
    },
    {
      dataIndex: 'albumList',
      width: 50,
      search: false,
      align: 'center',
      render: (_, record) => (
        <Image
          src={(record.albumList as unknown) as string}
          fallback="data:image/png;base64,"
        />
      ),
    },
    spuCodeColumns,

    {
      title: '规格',
      dataIndex: 'spec',
    },
    {
      title: '单位',
      dataIndex: 'unitName',
    },
    stateColumns,
    optionColumns({
      modify: async ({ record }) => {
        history.push(`/bas/product/${record.spuId}`);
      },
      del: async ({ record }) => {
        await delProduct([record.spuId]);
      },
    }),
  ];
  return (
    <>
      <ProTable<BAS.Spu>
        options={false}
        search={baseSearch({
          url: `/bas/product/new?cateId=${cateId}`,
        })}
        rowSelection={{}}
        tableAlertOptionRender={({ selectedRowKeys }) => {
          return tableAlertOptionRenderDom([
            <BatchDel
              key="del"
              onConfirm={async () => {
                await delProduct(selectedRowKeys);
                actionRef.current?.reload();
              }}
            />,
          ]);
        }}
        params={{ cateId }}
        rowKey="spuId"
        actionRef={actionRef}
        columns={columns}
        request={async (params) => {
          const response = await queryProducts({
            ...params,
            pageNumber: params.current,
            queryFilter: params,
          });
          return {
            data: response.data.rows,
            success: response.code === 0,
            total: response.data.total,
          };
        }}
      />
    </>
  );
};
export default () => {
  const [cateId, setCateId] = useState<K>();
  console.log(cateId);
  return (
    <GlobalWrapper type="list">
      <PageContainer
        title={false}
        content={
          <ProCard split="vertical">
            <ProCard colSpan="384px">
              <div style={{ height: 25 }} />
              <ProdcutTypeTable inSpuPage cateId={cateId} setcateId={setCateId} />
            </ProCard>
            <ProCard>
              <ProductsTable cateId={cateId} />
            </ProCard>
          </ProCard>
        }
      />
      ;
    </GlobalWrapper>
  );
};
